import LeanExpenses from "./components/Expenses/LeanExpenses";
import ExpenseForm from "./components/NewExpense/ExpenseForm";
import ActivateSight from "./ActivateSight";
import { useState } from "react";
const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];
function App() {
  // Set up state
  const [expenseObject, setExpenseObject] = useState(DUMMY_EXPENSES);
  const saveExpenseDataHandler = function (savedFormData) {
    const expenseData = {
      ...savedFormData, // save the data already present in the parameter
      id: Math.random().toString(), // add a unique ID
      // the parameter will equal the object of data that's collected in ExpenseForm.js
    };
    (() => {
      DUMMY_EXPENSES.push(expenseData);
    })();
    setExpenseObject(DUMMY_EXPENSES);
  };

  return (
    <div>
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
      <LeanExpenses expenseObj={expenseObject} />
      <ActivateSight />
    </div>
  );
}
export default App;
