import './ExpensesList.css' 
import ExpenseItem from "./ExpenseItem";


function ExpensesList(props){
   //% Filter the expenses object based on the filter choice
   const filteredExpenses = props.expenseObj.filter((obj) => {
    // "Any" => no filter
    if (props.filteredYear === "Any") return obj;
    // Year selected => filter according to it
    return obj.date.getFullYear().toString() === props.filteredYear;
  });

  //% Returna  different JSX depending on whether filteredExpenses is empty or not
  if(filteredExpenses.length===0) return 

  let conditionalJSX = <p style={{ color: 'white' }}>No expenses found</p>;
  if (filteredExpenses.length > 0) {
    conditionalJSX = filteredExpenses.map((obj) => (
      <ExpenseItem
        key={obj.id}
        title={obj.title}
        amount={obj.amount}
        date={obj.date}
      />
    ));
  }
	return(
    <div>d</div>
	)
}
export default ExpensesList
